import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent {
  public uploadSaveUrl: string = "/api/FileUpload/SaveFile";

  public userPic: string = ' ';
  
  setFilename(file: any){
    this.userPic = file[0].name;
  }

  successEventHandler(e: any) {
      // Get the file URI from the response
      this.userPic = e.response.json().uri;
  }



}
